*{
    box-sizing: border-box;
    margin: 0px;
}

.container {
    width: 1700px;
    max-width: 100%;
    margin-left: auto;
    margin-right: auto;
    border: 1px solid black;
    display: flex;
    height: 100%;
}

.nav-box {
    width: 30%;
    border-right: 1px solid black;
    height: 100%;
}

.clock {
    border-bottom: 1px solid black;
    font-size: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 5%
}

.building-box {
    height: 95%;
    overflow-x: hidden;
    overflow-y: scroll;
}

.building {
    height: 23%;
    border-bottom: 1px solid black;
    display: flex;
    padding-left: 3px;
    position: relative;
}

.building-image {
    width: 50%;
    margin-top: 25px;
}

.type-building {
    position: absolute;
    padding: 5px;
    font-weight: 700;
    text-align: center;
    width: 100%;
    margin-top: 5px;
}

.type-building-castle{
    position: absolute;
    font-weight: 700;
    text-align: center;
    top: 5px;
    width: 100%;
    margin-top: 5px;
}

.new-building {
    height: 20%;
    width: 20%;
    margin: 5px;
}

.sawmill {
    background-image: url(fcaaeae8fdb92bfe575778bdd975cd94.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.quarry {
    background-image: url(416703a763f1c54275dcc1e10ce50257.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.gold-mine {
    background-image: url(4809e072834026b206e5cbae859b1256.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat; 
}

.farm {
    background-image: url(2310b97702cde8d49aa6d5f49346be35.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.castle {
    background-image: url(3714968b12afd9a15ac293dc1900546a.png);
    background-position: 50% 50%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.wood-warehouse {
    background-image: url(61752a93854185ed222c17cc9f31c5e4.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.stone-warehouse {
    background-image: url(82454aac3fa724bc21dbdd4b04186cf3.png);
    background-position: 40% 40%; 
    background-size: contain;
    background-repeat: no-repeat;
}

.castle-box {
    height: 30%;
}

.castle-box .bulding-expense-with-button {
    margin-top: 20px;
}

.castle-box .bulding-image {
    margin-top: 15px;
}

.board .castle {
    width: 20%;
    height: 30%;
}

.expense {
    margin-top: 60px;
}

.button {
    height: 25px;
    width: 100px;
    border: 1px solid black;
    margin-left: 25px;
    text-align: center;
    padding-top: 3px;
    border-radius: 10px;
    margin-top: 20px;
}

.button:hover {
    background-color: rgb(239, 247, 234);
    cursor: pointer;
}

.board-box {
    width: 70%;
}

.win-box {
    position: absolute;
    height: 100%;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;  
    flex-direction: column;
    opacity: 0;
    z-index: 1;
}

.win {
    font-size: 100px;
    color: #f93939;
    transition: 0.5s ease-in-out all;
}

.win-box .clock {
    margin-top: 20px;
    font-size: 60px;
    color: #f93939;
    border: none;
}

.timer {
    width: 100%;
    font-size: 25px;
    text-align: center;
    position: absolute;
}

.board {
    height: 94%;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    position: relative;
    margin-top: 10px;
    justify-content: flex-start;
    align-content: flex-start;
    overflow-x: hidden;
    overflow-y: scroll;
}

.info-box {
    height: 5%;
    width: 100%;
    border-top: 1px solid black;
    display: flex;
    align-items: center;
}

.info-small-box {
    width: 14%;
    display: flex;
    margin-left: 20px;
}

.info-small-box-worker {
    width: 30%;
}

.amount-wood {
    font-size: 30px; 
}

.wood {
    font-weight: 700; 
}

.stone {
    font-weight: 700; 
}

.gold {
    font-weight: 700; 
}

.worker {
    font-weight: 700; 
}

.opacity-1 {
    opacity: 1;
}

.opacity-05 {
    opacity: 0.5;
}
